properties.widgets.FileAnonymizerBatchSink-batchsin:
{
  "outputs": [],
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [
    {
      "label": "Anonymization Configuration",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "policyUrl",
          "label": "Policy URL"
        },
        {
          "widget-type": "textbox",
          "name": "identity",
          "label": "Identity"
        },
        {
          "widget-type": "password",
          "name": "sharedSecret",
          "label": "Shared Secret"
        },
        {
          "widget-type": "textbox",
          "name": "trustStorePath",
          "label": "Trust Store Path"
        },
        {
          "widget-type": "textbox",
          "name": "cachePath",
          "label": "Cache Path"
        }
      ]
    },
    {
      "label": "File Structure",
      "properties": [
        {
          "widget-type": "select",
          "name": "format",
          "label": "File Format",
          "widget-attributes": {
            "default": "CSV",
            "values": ["CSV"]
          }
        },
        {
          "widget-type": "select",
          "name": "ignoreHeader",
          "label": "Ignore Header Row",
          "widget-attributes": {
            "default": "No",
            "values": [
              "Yes",
              "No"
            ]
          }
        },
        {
          "name": "fieldList",
          "widget-type": "ds-multiplevalues",
          "label": "Anonymize Fields",
          "widget-attributes": {
            "values-delimiter": ":",
            "delimiter": ",",
            "placeholders": [
              "Input Field",
              "Anonymize (Yes/No)",
              "Anonymize Format (SSN/CC)"
            ],
            "numValues": "3"
          }
        }
      ]
    },
    {
      "label": "Google Cloud Storage Parameters",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "path",
          "label": "Path",
          "widget-attributes": {"placeholder": "gs://<bucket>/path/to/output"}
        },
        {
          "widget-type": "textbox",
          "name": "suffix",
          "label": "Path Suffix",
          "widget-attributes": {"default": "yyyy-MM-dd-HH-mm"}
        },
        {
          "widget-type": "select",
          "name": "proxyType",
          "label": "Proxy Type",
          "widget-attributes": {
            "default": "NONE",
            "values": [
              "SOCKS",
              "HTTP",
              "NONE"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "proxy",
          "label": "Proxy Connection",
          "widget-attributes": {"placeholder": "host:port"}
        }
      ]
    },
    {
      "label": "Google Cloud Storage Credentials",
      "properties": [{
        "widget-type": "textbox",
        "name": "serviceFilePath",
        "label": "Service Account File Path",
        "widget-attributes": {"default": "auto-detect"}
      }]
    },
    {
      "label": "General",
      "properties": [{
        "widget-type": "textbox",
        "name": "bufferSize",
        "label": "Buffer Size",
        "widget-attributes": {"default": "1024"}
      }]
    }
  ],
  "display-name": "File Anonymizer"
}
